export function HomepageHero({
  title = 'This is the Hero Title',
  description = 'This is the Hero Description. It can be as long as you want.',
  buttons = [],
}: HeroProps) {
  const hasHTML = /<[^>]+>/.test(title);

  return (
    <Flex direction="column" align="start" className={styles.root}>
      <HeroBackground />
      <Container className={styles.container}>
        <Flex direction="column" align="start" justify="center" className={styles.content}>
          {hasHTML ? (
            <h2 className={styles.title} dangerouslySetInnerHTML={{ __html: title }} />
          ) : (
            <h2 className={styles.title}>{title}</h2>
          )}
          <p className={cx(styles.description, 'headlineS')}>{description}</p>
          {buttons.length > 0 && (
            <div className={styles.buttonContainer}>
              {buttons.map((button) => (
                <Button
                  key={button.label}
                  href={button.href}
                  icon={button.icon}
                  size="medium"
                  width="full"
                  className={styles.button}>
                  {button.label}
                </Button>
              ))}
            </div>
          )}
        </Flex>
      </Container>
    </Flex>
  );
}
